package com.syntax.reviewclass4;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Batch 12 is great");
		System.out.println("Batch 12 is great");
		System.out.println("Batch 12 is great");
		System.out.println("Batch 12 is great");
		System.out.println("**********************");
		int counter=1;
		
		while(counter<=4) {
			System.out.println("Batch 12 is great"+ counter);
			counter++;
		}
		
		
	
	}

}
